// Mode test: true = envoie uniquement √† shatit@gmail.com
// Le body du webhook est dans $input.first().json.body pour responseMode
const inputData = $input.first().json;
const testMode = inputData.test_mode === true || inputData.body?.test_mode === true;

// Calcul fen√™tre semaine (7 jours en arri√®re)
const today = new Date();
const weekAgo = new Date(today);
weekAgo.setDate(today.getDate() - 7);
weekAgo.setHours(0, 0, 0, 0);

return [{
  json: {
    week_start: weekAgo.toISOString(),
    week_label: `Semaine du ${weekAgo.toLocaleDateString('fr-FR', {day: 'numeric', month: 'long'})}`,
    test_mode: testMode
  }
}];

// Filtrer les articles de cette semaine
const input = items[0].json;
const initData = $('Init Week').first().json;
const weekStart = new Date(initData.week_start);
const weekLabel = initData.week_label;
const testMode = initData.test_mode;
const posts = input.posts || [];

const thisWeekPosts = posts.filter(post => {
  const pubDate = new Date(post.published_at);
  return pubDate >= weekStart;
});

if (thisWeekPosts.length === 0) {
  return [{ json: { skip: true, reason: 'Aucun article cette semaine' }}];
}

// Grouper par agent/auteur
const byAuthor = {};
thisWeekPosts.forEach(post => {
  const author = post.authors?.[0]?.name || 'Digital Humans';
  if (!byAuthor[author]) byAuthor[author] = [];
  byAuthor[author].push({
    title: post.title,
    excerpt: post.custom_excerpt || post.excerpt || '',
    url: `https://digital-humans.fr/blog/${post.slug}`,
    published_at: post.published_at
  });
});

return [{
  json: {
    skip: false,
    week_label: weekLabel,
    total_articles: thisWeekPosts.length,
    by_author: byAuthor,
    posts: thisWeekPosts,
    test_mode: testMode
  }
}];

// G√©n√©rer le HTML de la newsletter
const data = items[0].json;

const agentColors = {
  'Sophie Chen': '#8B5CF6', 'Olivia Parker': '#3B82F6', 'Marcus Johnson': '#F97316',
  'Diego Martinez': '#EF4444', 'Zara Thompson': '#22C55E', 'Raj Patel': '#EAB308',
  'Elena Vasquez': '#6B7280', 'Jordan Blake': '#1E40AF', 'Aisha Okonkwo': '#92400E',
  'Lucas Fernandez': '#D946EF'
};

let articlesHtml = '';
for (const [author, posts] of Object.entries(data.by_author)) {
  const color = agentColors[author] || '#6366F1';
  articlesHtml += `<div style="margin-bottom:24px;"><h3 style="color:${color};margin-bottom:12px;font-size:18px;">${author}</h3>`;
  for (const post of posts) {
    articlesHtml += `<div style="margin-bottom:16px;padding-left:16px;border-left:3px solid ${color};"><a href="${post.url}" style="color:#1a1a1a;text-decoration:none;font-weight:600;font-size:16px;">${post.title}</a><p style="color:#666;margin-top:4px;font-size:14px;">${(post.excerpt || '').substring(0,150)}...</p></div>`;
  }
  articlesHtml += '</div>';
}

// Bandeau TEST si mode test
const testBanner = data.test_mode ? '<div style="background:#FEF3C7;color:#92400E;padding:12px;text-align:center;margin-bottom:20px;border-radius:8px;">‚ö†Ô∏è MODE TEST - Cet email n\'est envoy√© qu\'√† shatit@gmail.com</div>' : '';

const html = `<!DOCTYPE html><html><head><meta charset="utf-8"></head><body style="font-family:-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,sans-serif;max-width:600px;margin:0 auto;padding:20px;background:#f5f5f5;"><div style="background:white;padding:32px;border-radius:12px;">${testBanner}<div style="text-align:center;margin-bottom:32px;"><h1 style="color:#6366F1;margin-bottom:8px;">üì¨ Newsletter Digital Humans</h1><p style="color:#666;">${data.week_label}</p></div><p style="font-size:16px;color:#333;margin-bottom:24px;">Cette semaine, nos agents ont publi√© <strong>${data.total_articles} article${data.total_articles > 1 ? 's' : ''}</strong> pour vous aider √† ma√Ætriser Salesforce.</p>${articlesHtml}<div style="margin-top:32px;padding-top:24px;border-top:1px solid #eee;text-align:center;"><a href="https://digital-humans.fr/blog" style="display:inline-block;background:#6366F1;color:white;padding:12px 24px;border-radius:8px;text-decoration:none;font-weight:600;">Voir tous les articles</a></div><div style="margin-top:32px;text-align:center;color:#999;font-size:12px;"><p>Digital Humans - L'IA au service du d√©veloppement Salesforce</p><p><a href="https://digital-humans.fr" style="color:#6366F1;">digital-humans.fr</a></p></div></div></body></html>`;

return [{ json: { subject: `üì¨ Newsletter Digital Humans - ${data.week_label}`, html, total_articles: data.total_articles, test_mode: data.test_mode }}];
